
tinymce.PluginManager.add('ack', function(e) {
    function openmanager() {
        var win, data;
        var modulo, id;
        modulo = $('input#infoPagina').data('info')['modulo'];
        id = $('input#infoPagina').data('info')['id'];
        win = e.windowManager.open({

            title: 'Gerenciador de arquivos',
            data: data,
            classes: 'filemanager',
            url: siteURL+'/filemanager/index/' +e.id+ '/'+ modulo +'/'+ id,
            filetype: 'image',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    function openmanagerimage() {
        var win, data;
        var modulo, id;
        modulo = $('input#infoPagina').data('info')['modulo'];
        id = $('input#infoPagina').data('info')['id'];
        win = e.windowManager.open({

            title: 'Gerenciador de Imagens',
            data: data,
            classes: 'filemanager',
            url: siteURL+'/filemanager/imagens/' +e.id+ '/'+ modulo +'/'+ id,
            filetype: 'image',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    function openmanagervideo() {
        var win, data;
        var modulo, id;
        modulo = $('input#infoPagina').data('info')['modulo'];
        id = $('input#infoPagina').data('info')['id'];
        win = e.windowManager.open({

            title: 'Gerenciador de Vídeos',
            data: data,
            classes: 'filemanager',
            url: siteURL+'/filemanager/videos/' +e.id+ '/'+ modulo +'/'+ id,
            filetype: 'image',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    e.addButton('addImage', {
        text: 'Adicionar Imagem',
        icon: "image",
        onclick: function() {
            // Open window
            e.windowManager.open({
                title: 'Adicionar imagem',
                body: [
                    {
                        type: 'container',
                        layout: 'flex',
                        classes: 'combobox has-open',
                        label: 'Source',
                        direction: 'row',
                        items: [{
                            name: 'src',
                            type: 'textbox',
                            filetype: 'image',
                            size: 35,
                            classes: 'img_' + e.id,
                            autofocus: true
                        }, {
                            name: 'upl_img',
                            type: 'button',
                            classes: 'btn open',
                            icon: 'browse',
                            onclick: openmanagerimage,
                            tooltip: 'Upload image'
                        }]
                    }
                ],
                onsubmit: function(c) {
                    // Insert content when the window form is submitted
                    e.insertContent('<p>'+c.data.src+'</p>');
                }
            });
        }
    });

    e.addButton('addVideo', {
        text: 'Adicionar Vídeo',
        icon: "media",
        onclick: function() {
            // Open window
            e.windowManager.open({
                title: 'Adicionar Vídeo',
                body: [
                    {
                        type: 'container',
                        layout: 'flex',
                        classes: 'combobox has-open',
                        label: 'Source',
                        direction: 'row',
                        items: [{
                            name: 'src',
                            type: 'textbox',
                            filetype: 'image',
                            size: 35,
                            classes: 'vid_' + e.id,
                            autofocus: true
                        }, {
                            name: 'upl_video',
                            type: 'button',
                            classes: 'btn open',
                            icon: 'browse',
                            onclick: openmanagervideo,
                            tooltip: 'Upload video'
                        }]
                    }
                ],
                onsubmit: function(r) {
                    // Insert content when the window form is submitted
                    e.insertContent('<p>'+r.data.src+'<p>');
                }
            });
        }
    });


});